mapper.select:
select
  company_id
from company_info


      and root_company_id = #{rootCompanyId}


        and province_code = #{provinceCode}


        and city_code = #{cityCode}


        and area_code = #{areaCode}

select
from company_info where 1=1
and company_id in #{companyId}

select
from company_info where 1=1
and company_id = #{companyId}

SELECT

FROM
company_info

    and is_delete = 2

      and company_name like #{companyName}


        and province_code = #{provinceCode}


        and city_code = #{cityCode}


        and area_code = #{areaCode}


        and legal_name like #{legalName}


        and legal_phone like #{legalPhone}

    order by create_time

SELECT ,province.province_name, city.city_name, branch.company_name siteProvinceName, cityBranch.city_branch_name siteCityName, brand.brand_name, orderIndex.count brand_count FROM company_info info LEFT JOIN province province ON info.province_code = province.province_code LEFT JOIN city city ON info.city_code = city.city_code left join pc_branch_company branch on info.branch_company_code = branch.branch_company_code left join pc_city_branch cityBranch on info.city_branch_code = cityBranch.city_branch_code left join (select company_id, agency_code, count(id) from pc_dealer_brand_info where audit_status = 0 group by company_id, agency_code) orderIndex on info.company_id = orderIndex.company_id LEFT JOIN ( SELECT company_id, agency_code, string_agg ( brand_name, '、' ) brand_name FROM pc_dealer_brand_info WHERE audit_status = 3 GROUP BY company_id, agency_code ) brand ON info.company_id = brand.company_id AND info.dealer_company_id = brand.agency_code
and info.company_classify = 2
AND info.branch_company_code = #{branchCompanyCode}
AND info.city_branch_code = #{cityBranchCode}
AND info.dealer_company_id = #{dealerCompanyId}
AND info.company_name like #{param}
AND info.audit_status = #{auditStatus}
AND info.province_code = #{provinceCode}
AND info.city_code = #{cityCode}
order by orderIndex.count, info.update_time

SELECT info.company_id, info.role_id, role.role_name, expand.comapny_nature as comapny_nature, info.company_name, contract.start_time, contract.end_time, contract.signed_time, contract.contract_number, info.legal_name, branch.organization_id as site_code, branch.organization_text as site_city_name, expand.contact_name, expand.contact_phone, info.deposit_money, info.audit_status FROM company_info info LEFT JOIN hr_organization_entity branch ON info.site_company_id = branch.organization_id LEFT JOIN company_info_expand expand ON info.company_id = expand.company_id LEFT JOIN user_role_set role ON info.role_id = role.role_code left JOIN ( SELECT id, contract_number, contract_status, signed_time, start_time, end_time, create_time, company_id, contract_url, contract_remark, role_id FROM pc_contract_info WHERE id IN ( SELECT max( id ) FROM pc_contract_info GROUP BY company_id ) ) contract ON info.company_id = contract.company_id and contract.role_id = info.role_id WHERE info.company_classify = 2
and info.site_company_id in #{item}
AND info.site_company_id = #{siteCode}
AND info.company_id = #{companyId}
AND to_char(contract.start_time, 'YYYY-MM-DD') = #{startTime}
AND info.role_id = #{roleId}
AND ( info.company_name like #{param} OR info.legal_name like #{param} OR expand.contact_name like #{param} OR expand.contact_phone like #{param} )
AND info.audit_status = #{auditStatus}
AND to_char(contract.signed_time, 'YYYY-MM-DD') = #{signedTime}
order by ${key} ${value}
order by info.update_time desc

SELECT info.company_id, info.roleId, role.role_name, expand.comapny_nature as comapny_nature, info.company_name, contract.start_time, contract.end_time, contract.signed_time, contract.contract_number, info.legal_name, branch.organization_id as site_code, branch.organization_text as site_city_name, expand.contact_name, expand.contact_phone, info.deposit_money, info.audit_status FROM company_info info LEFT JOIN hr_organization_entity branch ON info.site_company_id = branch.organization_id LEFT JOIN company_info_expand expand ON info.company_id = expand.company_id LEFT JOIN user_role_set role ON info.role_id = role.role_code left JOIN ( SELECT id, contract_number, contract_status, signed_time, start_time, end_time, create_time, company_id, contract_url, contract_remark, role_id FROM pc_contract_info WHERE id IN ( SELECT max( id ) FROM pc_contract_info GROUP BY company_id ) ) contract ON info.company_id = contract.company_id and contract.role_id = info.role_id WHERE info.company_classify = 2
and info.site_company_id in #{item}
AND info.company_id = #{companyId}
AND to_char(contract.start_time, 'YYYY-MM-DD') = #{startTime}
AND info.role_id = #{roleId}
AND ( info.company_name like #{param} OR info.legal_name like #{param} OR expand.contact_name like #{param} OR expand.contact_number like #{param} )
AND info.audit_status = #{auditStatus}
AND to_char(contract.signed_time, 'YYYY-MM-DD') = #{signedTime}
and info.company_id in #{item}
order by ${key} ${value}
order by info.update_time desc

mapper.update:
update company_info


        is_delete = #{isDelete,jdbcType=SMALLINT},


        update_time = #{updateTime,jdbcType=TIMESTAMP},


        platform_type = #{platformType,jdbcType=SMALLINT}


where company_id = #{companyId}

mapper.select:
SELECT info.company_id, info.role_id, role.role_name, expand.comapny_nature AS comapny_nature, info.company_name, info.legal_name, expand.contact_name, expand.contact_phone, info.deposit_money, info.audit_status, contract.brand_name, province.province_name as provinceName, city.city_name as cityName, area.area_name AS areaName FROM company_info info left join province province on info.province_code = province.province_code left join city city on city.city_code = info.city_code left join area area on area.area_code = info.area_code LEFT JOIN company_info_expand expand ON info.company_id = expand.company_id LEFT JOIN user_role_set role ON info.role_id = role.role_code LEFT JOIN ( SELECT company_id, string_agg ( brand_name, ' ' ) brand_name FROM pc_agency_contract_terms WHERE contract_number IN ( SELECT contract_number FROM pc_agency_contract WHERE STATUS = 8 ) GROUP BY company_id ) contract ON info.company_id = contract.company_id WHERE info.company_classify = 2
and info.site_company_id in #{item}
AND info.site_company_id = #{siteCode}
AND info.company_id = #{companyId}
AND info.role_id = #{roleId}
AND info.company_name like #{param}
AND info.audit_status = #{auditStatus}
order by info.update_time desc
