mapper.select:
SELECT sum(CASE WHEN role_id = 'CS' THEN 1 ELSE 0 END) as steward, sum(CASE WHEN role_id = 'CP' THEN 1 ELSE 0 END) as projectManager, sum(CASE WHEN role_id = 'CQ' THEN 1 ELSE 0 END) as qualityInspector, sum(CASE WHEN role_id = 'CM' THEN 1 ELSE 0 END) as foreman
from company_user_set where is_job = 0 and company_id in
#{listItem}

select cu.*, ro.role_name, co.company_name  from company_user_set cu
join user_role_set ro on cu.role_id = ro.role_code
join company_info co on cu.company_id = co.company_id where cu.user_id=#{userId}

select *, com.company_name from company_user_set us join company_info com on us.company_id = com.company_id


        and us.name like #{name}


        and us.phone like #{phone}


        and us.is_bind = #{isBind}


        and us.is_job = #{isJob}


        and us.provinve_code=#{provinceCode}


        and us.city_code=#{cityCode}


        and us.area_code=#{areaCode}


        and us.company_id=#{companyId}
