mapper.select:
select count(1) from (SELECT DISTINCT ou.project_no projectNo FROM order_user ou RIGHT JOIN construction_order co ON co.order_no = ou.order_no AND co.order_stage < #{orderStatus} RIGHT JOIN a_f_instance afi on afi.project_no = ou.project_no AND afi.config_no IN
#{configNo}
WHERE ou.user_id = #{userId})

SELECT DISTINCT ou.project_no projectNo, ou.order_no orderNo, proj.create_time appointmentTime, CONCAT(prov.province_name, ci.city_name, ar.area_name, proj.address_detail) address, bd.basics_name as type, proj.stage stage FROM order_user ou RIGHT JOIN construction_order co ON co.order_no = ou.order_no AND co.order_stage < #{orderStatus} RIGHT JOIN a_f_instance afi on afi.project_no = ou.project_no AND afi.config_no IN
#{configNo}
LEFT JOIN project proj ON proj.project_no = ou.project_no LEFT JOIN province prov ON prov.province_code = proj.province LEFT JOIN city ci ON ci.city_code = proj.city LEFT JOIN area ar ON ar.area_code = proj.area LEFT JOIN basics_data bd ON bd.basics_code = proj.style and bd.basics_group = 'DESIGN_STYLE' WHERE ou.user_id = #{userId} limit #{pageSize} offset #{pageNum}
