mapper.select:
SELECT P .address AS address, i.company_name AS companyName, o.order_no AS orderNo, P .create_time AS appointmentTime, P .address_detail AS addressDetail,
co.money AS reducedContractAmount, o.order_stage AS orderStage, b.big_name AS constructionProgress, b.is_adopt AS checkCondition, d.delay_time AS delayDays
FROM designer_order o LEFT JOIN construction_order co ON co.project_no = o.project_no
LEFT JOIN project_delay d ON d.project_no = o.project_no LEFT JOIN project_big_scheduling_details b ON b.project_no = o.project_no LEFT JOIN company_info i ON i.company_id = o.company_id LEFT JOIN project P ON P .project_no = o.project_no
WHERE o.project_no=#{projectOrderVO.projectNo} AND o.status=#{projectOrderVO.status}
limit #{pageSize} offset #{pageNum}

SELECT count(1) FROM designer_order o LEFT JOIN construction_order co ON co.project_no = o.project_no
LEFT JOIN project_delay d ON d.project_no = o.project_no LEFT JOIN project_big_scheduling_details b ON b.project_no = o.project_no LEFT JOIN company_info i ON i.company_id = o.company_id LEFT JOIN project P ON P .project_no = o.project_no
WHERE o.project_no=#{projectOrderVO.projectNo} AND o.status=#{projectOrderVO.status}
