mapper.select:
SELECT p.address, i.company_name, c.order_no, c.close_time, c.construction_stage, c.order_stage, p.order_source, u.name,
p.address_detail, u.phone, p.house_type, p.decoration_budget, p.house_room, p.house_office, p.house_toilet, p.balcony, p.area, p.style, p.reference_scheme, o.remark, o.type, p.plan_start_time, p.plan_end_time, c.construction_stage, c.money
FROM project p LEFT JOIN design_order o ON o.project_no=p.project_no LEFT JOIN construction_order c ON c.project_no=p.project_no LEFT JOIN company_info i ON i.company_id = p.company_id LEFT JOIN pc_user_info u ON u.id=p.owner_id WHERE p.project_no=#{projectNo} AND p.status=#{status}
